
-- -----------------------------
-- Table structure for `advers`
-- -----------------------------
DROP TABLE IF EXISTS `advers`;
CREATE TABLE `advers` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(20) NOT NULL,
  `create_time` varchar(15) NOT NULL,
  `update_time` varchar(15) NOT NULL,
  `url` varchar(100) NOT NULL,
  `images` varchar(200) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


-- -----------------------------
-- Table structure for `yxd_admin`
-- -----------------------------
DROP TABLE IF EXISTS `yxd_admin`;
CREATE TABLE `yxd_admin` (
  `userid` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(20) NOT NULL,
  `password` varchar(64) NOT NULL,
  `create_time` varchar(11) DEFAULT NULL,
  `update_time` varchar(11) DEFAULT NULL,
  `nickname` varchar(20) DEFAULT NULL,
  `encrypt` varchar(6) DEFAULT NULL,
  `groupid` int(11) NOT NULL,
  `verify` int(2) NOT NULL DEFAULT '1',
  PRIMARY KEY (`userid`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4;

